/* API to retrieve APP version info */
DAVE_APP_VERSION_t COMP_SLOPE_GEN_GetAppVersion(void)
{
  DAVE_APP_VERSION_t version;

  version.major = COMP_SLOPE_GEN_MAJOR_VERSION;
  version.minor = COMP_SLOPE_GEN_MINOR_VERSION;
  version.patch = COMP_SLOPE_GEN_PATCH_VERSION;

  return version;
}

/* This function initializes the APP */
COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_Init(COMP_SLOPE_GEN_t* handle_ptr)
{
  COMP_SLOPE_GEN_STATUS_t status;
  GLOBAL_CCU8_STATUS_t status_ccu8_global;
  GLOBAL_HRPWM_STATUS_t status_hrpwm_global;

  XMC_HRPWM_CSG_CONFIG_t csg_config;

  status = COMP_SLOPE_GEN_STATUS_FAILURE;
  status_ccu8_global = GLOBAL_CCU8_STATUS_FAILURE;
  status_hrpwm_global = GLOBAL_HRPWM_STATUS_FAILURE;

  XMC_ASSERT("COMP_SLOPE_GEN_Init:handle_ptr is NULL", (handle_ptr != NULL));

  if (COMP_SLOPE_GEN_UNINITIALIZED == handle_ptr->state)
  {
    status_ccu8_global = GLOBAL_CCU8_Init(handle_ptr->config_ptr->global_ccu8_handle);

    if (GLOBAL_CCU8_STATUS_SUCCESS == status_ccu8_global)
    {
      status_hrpwm_global = GLOBAL_HRPWM_Init(handle_ptr->config_ptr->global_hrpwm_handle);

      if (GLOBAL_HRPWM_STATUS_SUCCESS == status_hrpwm_global)
      {
        csg_config.cmp_config = *handle_ptr->config_ptr->csg_cmp_config;
        csg_config.dac_config = *handle_ptr->config_ptr->csg_dac_config;
        csg_config.sgen_config = *handle_ptr->config_ptr->csg_sgen_config;

        XMC_HRPWM_CSG_Init(handle_ptr->csg_slice_ptr, &csg_config);

        XMC_HRPWM_CSG_SelBlankingInput(handle_ptr->csg_slice_ptr, handle_ptr->config_ptr->csg_blanking_config);
        XMC_HRPWM_CSG_SelClampingInput(handle_ptr->csg_slice_ptr, handle_ptr->config_ptr->csg_clamping_config);
        XMC_HRPWM_CSG_StartSlopeGenConfig(handle_ptr->csg_slice_ptr, handle_ptr->config_ptr->csg_start_slope_config);

        XMC_HRPWM_CSG_SetSRNode(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CRSE, handle_ptr->config_ptr->sr_crse);
        XMC_HRPWM_CSG_SetSRNode(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CFSE, handle_ptr->config_ptr->sr_cfse);
        XMC_HRPWM_CSG_SetSRNode(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CSEE, handle_ptr->config_ptr->sr_csee);

        if ((bool) true == handle_ptr->config_ptr->int_cmp_rise)
        {
          XMC_HRPWM_CSG_EnableEvent(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CRSE);
        }
        if ((bool) true == handle_ptr->config_ptr->int_cmp_fall)
        {
          XMC_HRPWM_CSG_EnableEvent(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CFSE);
        }
        if ((bool) true == handle_ptr->config_ptr->int_clamp_state_enter)
        {
          XMC_HRPWM_CSG_EnableEvent(handle_ptr->csg_slice_ptr, XMC_HRPWM_CSG_IRQ_ID_CSEE);
        }

        if ((bool) true == handle_ptr->config_ptr->run_dac_at_init)
        {
          XMC_HRPWM_EnableComparatorShadowTransfer(handle_ptr->csg_module_ptr,
                                                   (uint32_t) handle_ptr->csg_shadow_transfer_msk);
          XMC_HRPWM_StartDac(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_dac_run_bit);
          __NOP();
          __NOP();
          __NOP();
          __NOP();
          XMC_HRPWM_StartComparator(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_cmp_run_bit);
          XMC_HRPWM_StartSlopeGeneration(
              handle_ptr->csg_module_ptr,
              ((uint32_t) handle_ptr->config_ptr->csg_dac_slope_start | (uint32_t) handle_ptr->config_ptr
                  ->csg_dac_prescaler_start));

          handle_ptr->state = COMP_SLOPE_GEN_RUNNING;
        }
        else
        {
          handle_ptr->state = COMP_SLOPE_GEN_INITIALIZED;
        }
        status = COMP_SLOPE_GEN_STATUS_SUCCESS;
      }
    }
  }
  else
  {
    status = COMP_SLOPE_GEN_STATUS_ALREADY_INITIALIZED;
    XMC_DEBUG("COMP_SLOPE_GEN_Init:COMP_SLOPE_GEN_STATUS_ALREADY_INITIALIZED");
  }

  return (status);
} /* end of COMP_SLOPE_GEN_Init() API */

/**********************************************************************************************************/

/* Starts the CSG slice. It also starts the DAC and slope generator.*/
COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_Start_CSG(COMP_SLOPE_GEN_t* handle_ptr)
{
  COMP_SLOPE_GEN_STATUS_t status;
  status = COMP_SLOPE_GEN_STATUS_FAILURE;

  XMC_ASSERT("COMP_SLOPE_GEN_Start_CSG:handle_ptr NULL", (handle_ptr != NULL));
  if ((COMP_SLOPE_GEN_INITIALIZED == handle_ptr->state) || (COMP_SLOPE_GEN_STOPPED == handle_ptr->state))
  {
    XMC_HRPWM_EnableComparatorShadowTransfer(handle_ptr->csg_module_ptr,
                                             (uint32_t) handle_ptr->csg_shadow_transfer_msk);
    XMC_HRPWM_StartComparator(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_cmp_run_bit);
    XMC_HRPWM_StartDac(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_dac_run_bit);
    XMC_HRPWM_StartSlopeGeneration(
        handle_ptr->csg_module_ptr,
        ((uint32_t) handle_ptr->config_ptr->csg_dac_slope_start | (uint32_t) handle_ptr->config_ptr
            ->csg_dac_prescaler_start));
    handle_ptr->state = COMP_SLOPE_GEN_RUNNING;
    status = COMP_SLOPE_GEN_STATUS_SUCCESS;
    XMC_DEBUG("COMP_SLOPE_GEN_Start_CSG:Comparator Slope Generator Started");
  }
  return (status);
} /* end of COMP_SLOPE_GEN_Start_CSG() API */

/**********************************************************************************************************/

/* Stops the CSG slice. It also stops the DAC and slope generator.*/
COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_Stop_CSG(COMP_SLOPE_GEN_t* handle_ptr)
{
  COMP_SLOPE_GEN_STATUS_t status;
  status = COMP_SLOPE_GEN_STATUS_FAILURE;

  XMC_ASSERT("COMP_SLOPE_GEN_Stop_CSG:handle_ptr NULL", (handle_ptr != NULL));
  if (COMP_SLOPE_GEN_UNINITIALIZED != handle_ptr->state)
  {
    XMC_HRPWM_StopComparator(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_cmp_run_bit);
    XMC_HRPWM_StopDac(handle_ptr->csg_module_ptr, (uint32_t) handle_ptr->config_ptr->csg_dac_run_bit);
    XMC_HRPWM_StopSlopeGeneration(
        handle_ptr->csg_module_ptr,
        ((uint32_t) handle_ptr->config_ptr->csg_dac_slope_stop | (uint32_t) handle_ptr->config_ptr
            ->csg_dac_prescaler_start));
    status = COMP_SLOPE_GEN_STATUS_SUCCESS;
    XMC_DEBUG("COMP_SLOPE_GEN_Stop_CSG:Comparator Slope Generator Stopped");
    handle_ptr->state = COMP_SLOPE_GEN_STOPPED;
  }
  return (status);
} /* end of COMP_SLOPE_GEN_Stop_CSG() API */
/**********************************************************************************************************/

COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_SetDACStartValue(COMP_SLOPE_GEN_t *handle_ptr, uint16_t start_value)
{
  COMP_SLOPE_GEN_STATUS_t status;
  status = COMP_SLOPE_GEN_STATUS_FAILURE;

  XMC_ASSERT("COMP_SLOPE_GEN_SetDACStartValue:handle_ptr NULL", (handle_ptr != NULL));
  if (COMP_SLOPE_GEN_UNINITIALIZED != handle_ptr->state)
  {
    if (start_value <= COMP_SLOPE_GEN_MAX_DAC_VALUE)
    {
      XMC_HRPWM_CSG_UpdateDACRefDSV1(handle_ptr->csg_slice_ptr, (uint32_t) start_value);
      XMC_HRPWM_EnableComparatorShadowTransfer(handle_ptr->csg_module_ptr,
                                               (uint32_t) handle_ptr->csg_shadow_transfer_msk);
      status = COMP_SLOPE_GEN_STATUS_SUCCESS;
      XMC_DEBUG("COMP_SLOPE_GEN_SetDACStartValue:CSG DAC start value set");
    }
  }
  return (status);
} /* end of COMP_SLOPE_GEN_SetDACStartValue() API */

/**********************************************************************************************************/

COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_SetDACStopValue(COMP_SLOPE_GEN_t *handle_ptr, uint16_t stop_value)
{
  COMP_SLOPE_GEN_STATUS_t status;
  status = COMP_SLOPE_GEN_STATUS_FAILURE;
  if (COMP_SLOPE_GEN_UNINITIALIZED != handle_ptr->state)
  {
    if (stop_value <= COMP_SLOPE_GEN_MAX_DAC_VALUE)
    {
      XMC_ASSERT("COMP_SLOPE_GEN_SetDACStopValue:handle_ptr NULL", (handle_ptr != NULL));
      XMC_HRPWM_CSG_UpdateDACRefDSV2(handle_ptr->csg_slice_ptr, (uint32_t) stop_value);
      status = COMP_SLOPE_GEN_STATUS_SUCCESS;
      XMC_DEBUG("COMP_SLOPE_GEN_SetDACStopValue:CSG DAC stop value set");
    }
  }
  return (status);

} /* end of COMP_SLOPE_GEN_SetDACStopValue() API */

/**********************************************************************************************************/

COMP_SLOPE_GEN_STATUS_t COMP_SLOPE_GEN_SetDACSwallowedPulses(COMP_SLOPE_GEN_t *handle_ptr, uint16_t swallowed_pulses)
{
  COMP_SLOPE_GEN_STATUS_t status;
  status = COMP_SLOPE_GEN_STATUS_FAILURE;

  XMC_ASSERT("COMP_SLOPE_GEN_SetDACSwallowedPulses:handle_ptr NULL", (handle_ptr != NULL));
  if (COMP_SLOPE_GEN_UNINITIALIZED != handle_ptr->state)
  {
    if (swallowed_pulses <= COMP_SLOPE_GEN_WINDOW_SIZE)
    {
      XMC_HRPWM_CSG_UpdatePulseClk(handle_ptr->csg_slice_ptr, (uint32_t) swallowed_pulses);
      XMC_HRPWM_EnableComparatorShadowTransfer(handle_ptr->csg_module_ptr,
                                               (uint32_t) handle_ptr->csg_shadow_transfer_msk);
      status = COMP_SLOPE_GEN_STATUS_SUCCESS;
      XMC_DEBUG("COMP_SLOPE_GEN_SetDACSwallowedPulses:CSG DAC pulse swallow value set");
    }
  }
  return (status);
} /* end of COMP_SLOPE_GEN_SetDACSwallowedPulses() API */

