/*
 * retrieve the version of the GLOBAL_HRPWM
 */
DAVE_APP_VERSION_t GLOBAL_HRPWM_GetAppVersion(void)
{
  DAVE_APP_VERSION_t version;

  version.major = GLOBAL_HRPWM_MAJOR_VERSION;
  version.minor = GLOBAL_HRPWM_MINOR_VERSION;
  version.patch = GLOBAL_HRPWM_PATCH_VERSION;

  return version;
}


/* This function initializes the HRPWM global */
GLOBAL_HRPWM_STATUS_t GLOBAL_HRPWM_Init(GLOBAL_HRPWM_t* handle)
{
  GLOBAL_HRPWM_STATUS_t status     = GLOBAL_HRPWM_STATUS_FAILURE;
  XMC_HRPWM_STATUS_t   lld_status = XMC_HRPWM_STATUS_ERROR;

  XMC_ASSERT("GLOBAL_HRPWM:Invalid or NULL pointer reference", (NULL != handle));

  if ((bool) false == handle->is_initialized)
  {
    /* Initialize HRPWM module */
      lld_status = XMC_HRPWM_Init(handle->module_ptr);

    if (XMC_HRPWM_STATUS_OK == lld_status)
    {
      handle->is_initialized = (bool) true;
      status = GLOBAL_HRPWM_STATUS_SUCCESS;
    }
    else
    {
      status = GLOBAL_HRPWM_STATUS_FAILURE;
    }
  }
  else
  {
    status = GLOBAL_HRPWM_STATUS_SUCCESS;
  }
  return status;
}
